import React, { useEffect, useState, useRef }  from "react";
import { View, Text, StyleSheet, Button, TextInput, TouchableOpacity, Image,ScrollView,  AppRegistry, TouchableWithoutFeedback, Keyboard } from "react-native";
import 'react-native-gesture-handler';
import { AuthContext } from "./context";
import { NavigationContainer, useIsFocused } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createDrawerNavigator } from "@react-navigation/drawer";
import { Camera } from 'expo-camera';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#0078D7',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    paddingHorizontal: 20,
    paddingVertical: 10,
    marginVertical: 10,
    borderRadius: 5
  },
  inputView:{
    width:"80%",
    backgroundColor:"#eee",
    borderRadius:25,
    height:50,
    marginBottom:20,
    justifyContent:"center",
    padding:20
  },
  inputView2:{
    backgroundColor:"#eee",
    borderRadius:25,
    width: 0,
    height: 0,
    marginBottom:20,
    justifyContent:"center",
    padding:20,
    opacity:0
  },
  inputText:{
    height:50,
    color:"#333"
  },
  inputText2:{
    width: 0,
    height: 0
  },
  errorText:{
    height:50,
    color:"#f00"
  },
   loginBtn:{
    width:"80%",
    backgroundColor:"#fb5b5a",
    borderRadius:25,
    height:50,
    alignItems:"center",
    justifyContent:"center",
    marginTop:40,
    marginBottom:10
  },
  logo:{
    width: 200,
     height: 60
  },
  camera: {
    flex: 1,
    width: "100%",
    justifyContent: 'flex-end',
    alignItems: 'center',
  },
  capture: {
    flex: 0,
    backgroundColor: '#fff',
    borderRadius: 5,
    padding: 15,
    paddingHorizontal: 20,
    alignSelf: 'center',
    margin: 20,
  },
});

const ScreenContainer = ({ children }, {TextInput}) => (
  <View style={styles.container}>{children}</View>
);








export const Home = ({ navigation }) => {  
  const [Barcode, setBarcode] = useState('');
  const [Output, setOutput] = useState('');
  const ref_input = useRef();
  const submitHandler = () => {
    setOutput(Barcode);
    setBarcode("")
    setTimeout(() => {
    ref_input.current.focus()
    if (!ref_input.current.isFocused){
      ref_input.current.focus()
    }
    }, 10);
    
   }

  return (
  React.useEffect(() => {

    const test = navigation.addListener('focus', () => {
      setTimeout(() => {
      ref_input.current.focus()
    }, 200);
    });

    return test;
  }, [navigation]),

    <ScreenContainer>
    <Text>{Output}</Text>
    <View style={styles.inputView2}>
      <TextInput
        autoFocus={true}
        clearTextOnFocus={true} 
        style={styles.inputText}
        placeholder="Barcode" 
        placeholderTextColor="#003f5c"
        onSubmitEditing={submitHandler} 
        onChangeText={(text) => setBarcode(text)}
        value={Barcode}
        ref={ref_input}
        showSoftInputOnFocus={false} />
   </View>

  </ScreenContainer>
)};


export const CameraScreen = ({ navigation }) => { 
  const [hasPermission, setHasPermission] = useState(null);
  const [photostate, setphotostate] = useState(null);
  const [type, setType] = useState(Camera.Constants.Type.back);
  const cameraInstance = useRef();
  const takePicture = async () => {
    if (cameraInstance) {
        const options = {quality: 1};
        const data = await cameraInstance.current.takePictureAsync(options);
        setphotostate(data.uri);
    }
  };
  useEffect(() => {
    (async () => {
      const { status } = await Camera.requestPermissionsAsync();
      setHasPermission(status === 'granted');
    })();
  }, []);

  if (hasPermission === null) {
    return <View />;
  }
  if (hasPermission === false) {
    return <Text>No access to camera</Text>;
  }
  return (
    <View style={styles.container}>
      <Camera style={styles.camera} type={type} ref={cameraInstance} ratio="16:9">
        <View style={styles.buttonContainer}>
          <TouchableOpacity
            style={styles.button}
            onPress={() => {
              setType(
                type === Camera.Constants.Type.back
                  ? Camera.Constants.Type.front
                  : Camera.Constants.Type.back
              );
            }}>
            <Text style={styles.text}> Flip </Text>
          </TouchableOpacity>
        </View>
        <View style={styles.buttonContainer}>
          <TouchableOpacity
            style={styles.button}
            onPress={() => takePicture()}>
            <Text style={styles.text}> Take Photo </Text>
          </TouchableOpacity>
          {photostate && <Image style={styles.photo} source={{uri: {photostate}}} />}
        </View>
      </Camera>
    </View>
  );
}

export const Details = ({ route }) => (
  <ScreenContainer>
    <Text>Details Screen</Text>
    {route.params.name && <Text>{route.params.name}</Text>}
  </ScreenContainer>
);

