import React, { useEffect, useState }  from "react";
import { View, Text, StyleSheet, Button, TextInput, TouchableOpacity, Image } from "react-native";
import 'react-native-gesture-handler';
import { AuthContext } from "./context";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createDrawerNavigator } from "@react-navigation/drawer";


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#0078D7',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    paddingHorizontal: 20,
    paddingVertical: 10,
    marginVertical: 10,
    borderRadius: 5
  },
  inputView:{
    width:"80%",
    backgroundColor:"#eee",
    borderRadius:25,
    height:50,
    marginBottom:20,
    justifyContent:"center",
    padding:20
  },
  inputText:{
    height:50,
    color:"#333"
  },
  errorText:{
    height:50,
    color:"#f00"
  },
   loginBtn:{
    width:"80%",
    backgroundColor:"#fb5b5a",
    borderRadius:25,
    height:50,
    alignItems:"center",
    justifyContent:"center",
    marginTop:40,
    marginBottom:10
  },
  logo:{
    width: 200,
     height: 60
  },
});

const ScreenContainer = ({ children }, {TextInput}) => (
  <View style={styles.container}>{children}</View>
);

export const Home = ({ navigation }) => (
  <ScreenContainer>
    <Text>Master List Screen</Text>
    <Button
      title="React Native by Example"
      onPress={() =>
        navigation.push("Details", { name: "React Native by Example " })
      }
    />
    <Button
      title="React Native School"
      onPress={() =>
        navigation.push("Details", { name: "React Native School" })
      }
    />
    <Button title="Drawer" onPress={() => navigation.toggleDrawer()} />
  </ScreenContainer>
);

export const Details = ({ route }) => (
  <ScreenContainer>
    <Text>Details Screen</Text>
    {route.params.name && <Text>{route.params.name}</Text>}
  </ScreenContainer>
);

export const Search = ({ navigation }) => (
  <ScreenContainer>
    <Text>Search Screen</Text>
    <Button title="Search 2" onPress={() => navigation.push("Search2")} />
    <Button
      title="React Native School"
      onPress={() => {
        navigation.navigate("Home", {
          screen: "Details",
          params: { name: "React Native School" }
        });
      }}
    />
  </ScreenContainer>
);

export const Search2 = () => (
  <ScreenContainer>
    <Text>Search2 Screen</Text>
  </ScreenContainer>
);

export const Profile = ({ navigation }) => {
  const { signOut } = React.useContext(AuthContext);

  return (
    <ScreenContainer>
      <Text>Profile Screen</Text>
      <Button title="Drawer" onPress={() => navigation.toggleDrawer()} />
      <Button title="Sign Out" onPress={() => signOut()} />
    </ScreenContainer>
  );
};

export const Splash = () => (
  <ScreenContainer>
    <Text>Loading...</Text>
    <TextInput/>
  </ScreenContainer>
);





export const SignIn = ({ navigation }) => {
  const { signIn } = React.useContext(AuthContext);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorOutput, setError] = useState('');


  let data = {
    method: 'POST',
    mode: "cors", // no-cors, cors, *same-origin *=default
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({"username": email,"password": password})
  };

  const getAuth = ({navigation}) => {
    console.log(data, navigation);
    return fetch('https://api-veen-e.ewipro.com/installer/authenticate/', data)
    .then((response) => {
        if(!response.ok) throw new Error(response.status);
        else return response.json();
      })
    .then((responseData) => {
       //getAuthToken(responseData.auth_key)
       setIsLoading(false);
    })
    .catch((error) => {
      setError("Unable to log in," + error.toString());
    });
  }

  
  return (
    <View style={styles.container}>
      <Image
        style={styles.logo}
        source={{uri: 'https://calculator.ewipro.com/img/ewistorelogo.png'}}
      />
    {/* <View style={styles.inputView} >
      <TextInput  
        style={styles.inputText}
        placeholder="Email..." 
        placeholderTextColor="#003f5c"
        onChangeText={text => setEmail(text)}/>
    </View>
    <View style={styles.inputView} >
      <TextInput  
        secureTextEntry
        style={styles.inputText}
        placeholder="Password..." 
        placeholderTextColor="#003f5c"
        onChangeText={text => setPassword(text)}/>
    </View> */}
    <TouchableOpacity>
    </TouchableOpacity>
    <TouchableOpacity style={styles.loginBtn} onPress={() => getAuth({navigation})}>
      <Text style={styles.loginText}>LOGIN</Text>
    </TouchableOpacity>
    <TouchableOpacity onPress={() => navigation.push('CreateAccount')}>
      <Text style={styles.loginText}>Signup</Text>
    </TouchableOpacity>
    <TouchableOpacity>
      <Text style={styles.errorText} >{errorOutput} </Text>
    </TouchableOpacity>


  </View>
  );
};

export const CreateAccount = () => {
  const { signUp } = React.useContext(AuthContext);

  return (
    <ScreenContainer>
      <Text>Create Account Screen</Text>
      <Button title="Sign Up" onPress={() => signUp()} />
    </ScreenContainer>
  );
};
