import React from "react";
import { View, Text, StyleSheet, Button } from "react-native";

import { AuthContext } from "./context";

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center"
    },
    button: {
        paddingHorizontal: 20,
        paddingVertical: 10,
        marginVertical: 10,
        borderRadius: 5
    }
});

const ScreenContainer = ({ children }) => ( <
    View style = { styles.container } > { children } < /View>
);

export const Home = ({ navigation }) => ( <
    ScreenContainer >
    <
    Text > Master List Screen < /Text> <
    Button title = "React Native by Example"
    onPress = {
        () =>
        navigation.push("Details", { name: "React Native by Example " })
    }
    /> <
    Button title = "React Native School"
    onPress = {
        () =>
        navigation.push("Details", { name: "React Native School" })
    }
    /> <
    Button title = "Drawer"
    onPress = {
        () => navigation.toggleDrawer() }
    /> <
    /ScreenContainer>
);

export const Details = ({ route }) => ( <
        ScreenContainer >
        <
        Text > Details Screen < /Text> {
            route.params.name && < Text > { route.params.name } < /Text>} <
                /ScreenContainer>
        );

        export const Search = ({ navigation }) => ( <
            ScreenContainer >
            <
            Text > Search Screen < /Text> <
            Button title = "Search 2"
            onPress = {
                () => navigation.push("Search2") }
            /> <
            Button title = "React Native School"
            onPress = {
                () => {
                    navigation.navigate("Home", {
                        screen: "Details",
                        params: { name: "React Native School" }
                    });
                }
            }
            /> <
            /ScreenContainer>
        );

        export const Search2 = () => ( <
            ScreenContainer >
            <
            Text > Search2 Screen < /Text> <
            /ScreenContainer>
        );

        export const Profile = ({ navigation }) => {
            const { signOut } = React.useContext(AuthContext);

            return ( <
                ScreenContainer >
                <
                Text > Profile Screen < /Text> <
                Button title = "Drawer"
                onPress = {
                    () => navigation.toggleDrawer() }
                /> <
                Button title = "Sign Out"
                onPress = {
                    () => signOut() }
                /> <
                /ScreenContainer>
            );
        };

        export const Splash = () => ( <
            ScreenContainer >
            <
            Text > Loading... < /Text> <
            /ScreenContainer>
        );

        export const SignIn = ({ navigation }) => {
            const { signIn } = React.useContext(AuthContext);

            return ( <
                ScreenContainer >
                <
                Image style = { styles.logo }
                source = {
                    { uri: 'https://calculator.ewipro.com/img/ewistorelogo.png' } }
                /> <
                View style = { styles.inputView } >
                <
                TextInput style = { styles.inputText }
                placeholder = "Email..."
                placeholderTextColor = "#003f5c"
                onChangeText = { text => setEmail(text) }
                /> <
                /View> <
                View style = { styles.inputView } >
                <
                TextInput secureTextEntry style = { styles.inputText }
                placeholder = "Password..."
                placeholderTextColor = "#003f5c"
                onChangeText = { text => setPassword(text) }
                /> <
                /View> <
                TouchableOpacity >
                <
                /TouchableOpacity> <
                TouchableOpacity style = { styles.loginBtn }
                onPress = {
                    () => signIn() } >
                <
                Text style = { styles.loginText } > LOGIN < /Text> <
                /TouchableOpacity> <
                TouchableOpacity onPress = {
                    () => navigation.push("CreateAccount") } >
                <
                Text style = { styles.loginText } > Signup < /Text> <
                /TouchableOpacity> <
                TouchableOpacity >
                <
                Text style = { styles.errorText } > { errorOutput } < /Text> <
                /TouchableOpacity> <
                /ScreenContainer>
            );
        };

        export const CreateAccount = () => {
            const { signUp } = React.useContext(AuthContext);

            return ( <
                ScreenContainer >
                <
                Text > Create Account Screen < /Text> <
                Button title = "Sign Up"
                onPress = {
                    () => signUp() }
                /> <
                /ScreenContainer>
            );
        };
